import { Card, Flex, Typography } from 'antd';
import "../../components/Header/Header.css";
import "./Calendar.css"

const data = {
    "events": [
        {
            "name": "Pertemuan Pertama",
            "date": "20 Januari 2023"
        },
        {
            "name": "Pertemuan Keluarga",
            "date": "1 Februari 2023"
        },
        {
            "name": "Lamaran",
            "date": "12 Maret 2023"
        },
        {
            "name": "Ulang Tahun Dila",
            "date": "26 Mei 1999"
        },
        {
            "name": "Akad Nikah",
            "date": "28 Mei 2023"
        },
        {
            "name": "Resepsi Nikah",
            "date": "14 Oktober 2023"
        },
    ]
}



function Calendar() {
    return (
        <div style={{ marginTop: "30px", marginBottom: "20px", marginLeft: "4rem", marginRight: "4rem", justifyContent: "center" }}>
            <h1 className="header-title" style={{ textAlign: "center" }}> Dila & Hanif calendar.</h1>
            {/* <Flex justify='center'> */}
            {data.events.map((event) =>
                <Card
                    hoverable
                    className='custom-box'
                    style={{ width: "300", marginTop: "20px" }}
                    styles={{ body: { padding: 0, overflow: 'hidden' } }}>
                    <Flex>
                        <img
                            alt="avatar"
                            src="http://picsum.photos/300/300"
                            style={{ display: 'block', width: 200 }} />
                        <Flex
                            align="center"
                            justify='flex-start'
                            style={{ padding: 32 }}>

                            <p>
                                <span style={{ fontSize: "22px", fontWeight: "bold" }}>{event.name}</span>
                                <br />
                                {event.date}
                            </p>

                        </Flex>
                    </Flex>
                </Card>)
            }

            {/* </Flex> */}
        </div >
    )
}

export default Calendar
