import { Card, Flex } from 'antd';
import "./Calendar.css"
import lamaran from '../../images/calendar/lamaran.jpg'

const data = {
    "events": [
        // {
        //     "name": "Pertemuan Pertama",
        //     "date": "20 Januari 2023",
        // },
        // {
        //     "name": "Pertemuan Keluarga",
        //     "date": "1 Februari 2023",
        // },
        {
            "name": "Lamaran",
            "date": "12 Maret 2023",
            "image": lamaran,
        },
        {
            "name": "Ulang Tahun Dila",
            "date": "26 Mei 1999",
            "image": lamaran,
        },
        {
            "name": "Akad Nikah",
            "date": "28 Mei 2023",
            "image": lamaran,
        },
        {
            "name": "Resepsi Nikah",
            "date": "14 Oktober 2023",
            "image": lamaran,
        },
    ]
}



function Calendar() {
    return (
        <div style={{ marginTop: "30px", paddingBottom: "30px", marginLeft: "10%", marginRight: "10%", justifyContent: "center" }}>
            <h1 className="header-title" style={{ textAlign: "center" }}> Dila & Hanif calendar.</h1>
            {/* <Flex justify='center'> */}
            {data.events.map((event) =>
                <Card
                    hoverable
                    className='custom-box'
                    style={{ width: "300", marginTop: "20px" }}
                    styles={{ body: { padding: 0, overflow: 'hidden' } }}
                    key={event.name}>
                    <Flex>
                        <img
                            alt="gambar"
                            src={event.image}
                            style={{ display: 'block', width: 200 }} />
                        <Flex
                            align="center"
                            justify='flex-start'
                            style={{ padding: 32 }}>

                            <p>
                                <span style={{ fontSize: "22px", fontWeight: "bold" }}>{event.name}</span>
                                <br />
                                {event.date}
                            </p>

                        </Flex>
                    </Flex>
                </Card>)
            }

        </div >
    )
}

export default Calendar
