import { Card, Flex, Typography } from 'antd';

const data = {
    "events": [
        {
            "name": "Pertemuan Pertama",
            "date": "20 Januari 2023"
        },
        {
            "name": "Pertemuan Keluarga",
            "date": "1 Februari 2023"
        },
        {
            "name": "Lamaran",
            "date": "12 Maret 2023"
        },
        {
            "name": "Ulang Tahun Dila",
            "date": "26 Mei 1999"
        },
        {
            "name": "Akad Nikah",
            "date": "28 Mei 2023"
        },
        {
            "name": "Resepsi Nikah",
            "date": "14 Oktober 2023"
        },
    ]
}



function Calendar() {
    return (
        <div style={{ marginTop: "30px", marginLeft: "4rem", marginRight: "4rem", justifyContent: "center" }}>
            <h1 className="header-title" style={{ textAlign: "center", marginBottom: "30px" }}> Dila & Hanif calendar.</h1>
            {/* <Flex justify='center'> */}
            {data.events.map((event) =>
                <Card
                    hoverable
                    style={{ width: "400", marginTop: "20px", backgroundColor: "var(--gray-color)", borderColor: "var(--gray-color)", color: "var(--white-color)" }}
                    styles={{ body: { padding: 0, overflow: 'hidden' } }}>
                    <Flex>
                        <img
                            alt="avatar"
                            src="http://picsum.photos/300/300"
                            style={{ display: 'block', width: 250 }} />
                        <Flex
                            align="center"
                            justify='flex-start'
                            style={{ padding: 32 }}>

                            <Typography.Title level={2}>
                                {event.name}
                                <br />
                                {event.date}
                            </Typography.Title>

                        </Flex>
                    </Flex>
                </Card>)}

            {/* </Flex> */}
        </div>
    )
}

export default Calendar
