import "./App.css";
import { useState } from "react";

import Mode from "../../components/Mode/Mode";
import Header from "../../components/Header/Header";
import { About } from "../../components/About/About";
import { Bio } from "../../components/Bio/Bio";
import { Footer } from "../../components/Footer/Footer";
import Project from "../../components/Project/Project";
import Calendar from "../calendar/Calendar";

function Content({ props }) {
  const { content } = props;
  switch (content) {
    case "calendar":
      return <Calendar />
    default:
      return <div>
        <Header />
        <About />
        <Bio />
        <Project />
        <Footer />
      </div>
  }
}

function Home() {
  const [whiteMode, setQuietMode] = useState(false);
  // const initialContent = false

  // const calendar = <Calendar />
  const [content, setContent] = useState(undefined)

  const handleModeChange = () => {
    setQuietMode(!whiteMode);
  };


  return (
    <div className={whiteMode ? "white-mode" : "dark-mode"}>
      <Mode setMode={whiteMode} handleModeChange={handleModeChange} setContent={setContent} />

      <Content props={content} />

      {/* {content} */}
      {/* <Calendar /> */}


    </div>
  );
}

export default Home;
